        trainings.add(Training(image = R.drawable.icon_press_9, title = "Упражнение №9", subtitle = "Подъем ноги с приведением колена сидя", description = "Сядьте на пол, опираясь на ладони, спину слегка отклоните назад. Согните в колене левую ногу, а правую выпрямите. На выдохе сделайте правой ногой мах вверх-вниз, а затем согните ее в колене и приведите к корпусу. Выполняйте целый подход на одну ногу. Упражнение приводит мышцы пресса в тонус и ускоряет жиросжигание, а также укрепляет внутреннюю мускулатуру живота.", time = 45, category = "Для пресса"))
        trainings.add(Training(image = R.drawable.icon_press_10, title = "Упражнение №10", subtitle = "Касание коленями пола в планке", description = "Встаньте в планку на предплечьях, спину держите на одном уровне с тазом, голову не запрокидывайте. На выдохе опустите одно колено вниз, касаясь пола, а затем вернитесь обратно и повторите другой ногой. Это упражнение на нижний пресс для женщин и мужчин не только прокачивает мышцы, но также улучшает выносливость тела и положительно влияет на осанку.", time = 30, category = "Для пресса"))
        trainings.add(Training(image = R.drawable.icon_cardio_1, title = "Упражнение №1", subtitle = "Джампинг джек с руками вдоль", description = "Поставьте ноги вместе, ладони зафиксируйте рядом друг с другом в районе ниже пояса. На выдохе совершите прыжок и расставьте ноги максимально широко. Одновременно с этим разведите руки через стороны, после чего зафиксируйте их параллельно полу в Т-образном положении. На вдохе подпрыгните снова и вернитесь в исходное положение. Отталкивание от пола совершайте за счет усилия носков. Элемент кардио-тренировки для мужчин и женщин не только обеспечивает жиросжигание, но и эффективно прорабатывает бедра, руки и плечи. Это помогает сделать тело более подтянутым и рельефным.", time = 45, category = "Для кардио"))
        trainings.add(Training(image = R.drawable.icon_cardio_2, title = "Упражнение №2", subtitle = "Скрестные прыжки + наклоны корпуса", description = "Расставьте ноги чуть шире плеч, а ладони сведите на затылке. Локти расправьте в противоположные друг от друга стороны. На счет «раз» сделайте прыжок и скрестите ноги так, чтобы левая конечность оказалась впереди, а правая – позади. На счет «два» вернитесь в исходное положение, а на «три» снова прыгните со скрещиванием ног, но поставьте вперед уже правую ногу (левую по аналогии отведите назад). На счет «четыре» примите исходное положение. Далее наклоните корпус влево и поднимите колено. Состыкуйте колено и локоть в пиковой точке. Такой же наклон совершите в правую сторону. Упражнение замечательно прорабатывает мышцы пояса и формирует стройную талию. Не меньше включаются в работу и мышцы ног.", time = 30, category = "Для кардио"))
        trainings.add(Training(image = R.drawable.icon_cardio_3, title = "Упражнение №3", subtitle = "Широкий присед + прыжок", description = "Расставьте ноги значительно шире плеч и немного разверните носки друг от друга. Выполните присед до уровня, пока углы сгибов в коленных суставах не окажутся прямыми. Далее с усилием оттолкнитесь и подпрыгните. В процессе прыжка сведите ноги вплотную друг к другу, а руки поднимите над собой через стороны. Сделайте новый прыжок, после чего расставьте ноги в исходное положение и повторите снова. Элемент кардио-тренировки на каждый день сочетает в себе силовую и кардио-нагрузку, благодаря чему способствует похудению и помогает проработать рельеф мышц бедер и ягодиц, увеличить их мышечный объем.", time = 35, category = "Для кардио", isRecommended = true))
        trainings.add(Training(image = R.drawable.icon_cardio_4, title = "Упражнение №4", subtitle = "Шаги в сторону с разведением рук", description = "Поставьте ноги вплотную друг к другу, а руки выпрямите перед собой. Сомкните ладони друг с другом. На выдохе отведите левую ногу в сторону и одновременно разведите верхние конечности в Т-образное положение. В ходе движения следите за тем, чтобы руки оставались параллельными полу. На вдохе вернитесь в исходное положение и проделайте аналогичное повторение, но уже с правой ногой. Упражнение помогает укрепить отводящие мышцы бедер, раскрыть грудную клетку, улучшить осанку, а также проработать мышцы плеч с большим акцентом на задние пучки дельтоидов. Это низкоударное упражнение поможет вам немного снизить пульс и передохнуть.", time = 40, category = "Для кардио"))
        trainings.add(Training(image = R.drawable.icon_cardio_5, title = "Упражнение №5", subtitle = "Двойной прыжок с разведением и вперед", description = "Поставьте ноги вплотную друг к другу, а руки вытяните вдоль туловища. На счет «раз» выполните прыжок вперед и поднимите верхние конечности, проведя их перед собой. Руки должны оставаться параллельными на протяжении всего движения. На счет «два» сделайте очередной прыжок и вернитесь в исходное положение. На «три» подпрыгните снова, расставьте ноги шире плеч и сведите руки над головой. На «четыре» вновь примите стартовую позицию и снова повторите два вида прыжка. Представленный элемент кардио-тренировки для мужчин и женщин оказывает тонизирующий эффект, помогает подтянуть мышцы рук и ног, развивает выносливость.", time = 30, category = "Для кардио"))
        trainings.add(Training(image = R.drawable.icon_cardio_6, title = "Упражнение №6", subtitle = "Джампинг джек + мах ногой", description = "В исходном положении поставьте ноги вплотную друг к другу, согнутые руки удерживайте вдоль туловища. Подпрыгните, расставьте ноги чуть шире плеч и отведите руки в боковые стороны. Далее сделайте повторный прыжок и вернитесь в исходное положение. Теперь поднимите правую ногу и отведите ее в левую сторону. Коснитесь носка пальцами левой руки. Снова сделайте прыжки по уже знакомой технике, после чего совершите мах со сменой сторон. Упражнение направлено на формирование подтянутой формы ног, помогает убрать дряблость с нижней части живота, улучшить качество тела в целом.", time = 30, category = "Для кардио"))
        trainings.add(Training(image = R.drawable.icon_cardio_7, title = "Упражнение №7", subtitle = "Джампинг джек + узкий присед", description = "Поставьте ноги близко друг к другу, а ладони скрепите в замке на уровне груди. Совершите присед до прямого угла в сгибе коленных суставов. В процессе приседания немного наклоните корпус вперед. Далее с усилием оттолкнитесь от пола, сделайте прыжок и разведите верхние и нижние конечности в стороны. Примите положение «звезды», после чего вновь подпрыгните и примите стартовое положение. Упражнение развивает взрывную силу ног, помогает укрепить мышцы и суставы плеч, разминает тазобедренный сустав. Одно из эффективнейших кардио-упражнений для сжигания жира.", time = 40, category = "Для кардио"))
        trainings.add(Training(image = R.drawable.icon_cardio_8, title = "Упражнение №8", subtitle = "Захлесты голеней с разведением рук", description = "Расставьте ноги немного шире плеч, а ладони прижмите к боковым частям бедер. Перенесите вес тела на левую ногу, после чего согните правую в колене и прижмите пятку к ягодице. Одновременно с этим поднимите руки вверх через стороны. Вернитесь в исходное положение. Далее перенесите вес тела на правую ногу и согните левую по аналогичной технике (не забудьте о движении рук). Элемент кардио-тренировки в домашних условиях подтягивает заднюю поверхность бедра, помогает укрепить ягодичные мышцы, а также задействует в работе мышцы рук и дельты.", time = 50, category = "Для кардио"))
        trainings.add(Training(image = R.drawable.icon_cardio_9, title = "Упражнение №9", subtitle = "Прыжок с поворотом таза + удар", description = "Поставьте ноги ближе друг к другу, ладони сожмите в кулаки и удерживайте их перед собой на уровне солнечного сплетения. Подпрыгните, поверните таз в правую сторону и совершите удар перед собой правой рукой. Вернитесь в исходное положение, после чего прыгните снова и поверните таз в левую сторону. Удар наносит уже левая рука. Такое упражнение помогает проработать косые мышцы, сформировать стройную зону пояса. Не меньше включаются в работу мышцы ног и рук.", time = 35, category = "Для кардио"))
        trainings.add(Training(image = R.drawable.icon_cardio_10, title = "Упражнение №10", subtitle = "Прыжки \"с резиночкой\"", description = "Поставьте руки на пояс, а ноги зафиксируйте ближе друг к другу. Далее подпрыгните на носочках и сделайте два шага со сменой ног. После этого примите исходное положение, прыгните снова и расставьте ноги на ширину плеч. Вновь оттолкнитесь носками от пола и вернитесь в стартовую позицию. Это завершающий элемент кардио-тренировки в домашних условиях, который акцентирует нагрузку в нижних конечностях, делает их сильнее, рельефнее и стройнее.", time = 35, category = "Для кардио"))
        trainings.add(Training(image = R.drawable.icon_arms_1, title = "Упражнение №1", subtitle = "Вращения руками", description = "Встаньте на колени и вытяните руки в стороны. Держите руки на одной линии, не сгибайте их в локтях. Начните одновременные вращения прямыми руками с небольшой амплитудой. Держите спину прямо, а корпус сохраняйте неподвижным. При выполнении вращений не размахивайте руками, темп должен быть ровным и размеренным. Простое упражнение для тренировки рук дома приведет в тонус мышцы плеч, а также разрабатывает суставы и связки, что важно для профилактики артрита.", time = 30, category = "Для рук"))
        trainings.add(Training(image = R.drawable.icon_arms_2, title = "Упражнение №2", subtitle = "Планка на коленях с подъемом рук к плечам", description = "Встаньте в планку на коленях, ладони поставьте под плечевыми суставами. Теперь оторвите одну руку от пола и коснитесь ладонью противоположного плеча. Вернитесь обратно и повторите другой рукой. Поочередно поднимайте руки, касаясь противоположного плеча ладонью, при этом сохраняя ровный темп. Выполняйте простое упражнение на руки в домашних условиях, чтобы подтянуть проблемные зоны, укрепить мышцы бицепсов, трицепсов и плечевого пояса.", time = 30, category = "Для рук"))
        trainings.add(Training(image = R.drawable.icon_arms_3, title = "Упражнение №3", subtitle = "Разгибание рук на коленях", description = "Встаньте на колени и согните руки в локтях перед собой. Предплечья должны быть параллельны полу и находится в одной плоскости с плечами, ладони направлены вниз. Теперь разведите руки в стороны, выпрямляя их в локтях, а затем снова сведите перед собой. В крайней точке не выпрямляйте руки полностью и выполняйте упражнение в среднем темпе без рывков. Включайте упражнение в тренировку на руки в домашних условиях, чтобы проработать плечи, проблемную заднюю и внутреннюю сторону рук.", time = 30, category = "Для рук"))
        trainings.add(Training(image = R.drawable.icon_arms_4, title = "Упражнение №4", subtitle = "Ножницы стоя на коленях", description = "Стоя на коленях, выпрямите руки перед собой, ладони направьте вниз. Спину держите прямо, подбородок приподнят. Теперь выполняйте простые ножницы с небольшой амплитудой. Для этого разведите руки в стороны, а затем сведите перед собой с нахлестом, меняя руки попеременно. Простое и эффективное упражнение для тренировки рук дома тонизирует всю поверхность плеч и предплечий, акцентируя на проработке трицепсов, зона которых часто является проблемной. Дополнительно работают грудные мышцы.", time = 40, category = "Для рук"))
        trainings.add(Training(image = R.drawable.icon_arms_5, title = "Упражнение №5", subtitle = "Отведение локтей из планки", description = "Встаньте в планку на коленях, а затем опуститесь на локти. Теперь поднимите правую руку, не разгибая ее в локте, и отведите в сторону. Вернитесь в исходное положение и повторите другой рукой. При выполнении не скручивайте корпус, работают только руки. Упражнение для рук без инвентаря поможет вам укрепить проблемную внутреннюю сторону, а также проработать трицепсы, приводя мышцы в тонус.", time = 30, category = "Для рук"))
        trainings.add(Training(image = R.drawable.icon_arms_6, title = "Упражнение №6", subtitle = "Подъем рук вверх с разгибанием локтей", description = "Встаньте на колени, а затем сядьте на пятки, принимая удобное положение. Разведите руки в стороны и согните в локтях под прямым углом, направляя предплечья вверх. Ладони раскрыты, направлены лицевой стороной вперед. Теперь поднимите руки вверх, разгибая их в локтях и соприкасаясь ребрами ладоней в верхней точке. Не разгибайте локти полностью, чувствуйте нагрузку в области бицепсов и трицепсов. Подъемы с разгибанием подтягивают внутреннюю сторону рук, а также укрепляют плечевой пояс и делают плечи округлыми.", time = 35, category = "Для рук"))
        trainings.add(Training(image = R.drawable.icon_arms_7, title = "Упражнение №7", subtitle = "Планка с переходом в собаку мордой вниз", description = "Встаньте в планку на прямых руках, следите, чтобы ладони находились точно под плечевыми суставами. Из планки поднимите таз вверх, прогибая спину и выпрямляя ноги. Опускайте голову между плеч, но не наклоняйте ее. Задержитесь на мгновение в позе собаки мордой вниз, а затем снова встаньте в планку, плавно опуская таз и корпус. Старайтесь не менять положение ног во время перехода из позы в позу. Комплексное упражнение для рук в домашних условиях укрепляет мышцы всего тела, особенно верха корпуса, делая вас сильнее и выносливее.", time = 30, category = "Для рук"))
        trainings.add(Training(image = R.drawable.icon_arms_8, title = "Упражнение №8", subtitle = "Пловец с пульсацией", description = "Лягте на живот, руки положите вдоль тела ладонями вверх. Приподнимите верх корпуса вместе с руками. Следите, чтобы руки были прямые и находились параллельно полу. В этом положении выполняйте пульсирующие движения обеими руками вверх-вниз, удерживая баланс. При этом не запрокидывайте голову, держите ее слегка опущенной. Упражнение прорабатывает верхнюю часть рук, в особенности трицепсы.", time = 40, category = "Для рук"))
        trainings.add(Training(image = R.drawable.icon_arms_9, title = "Упражнение №9", subtitle = "Развороты согнутых рук сидя", description = "Сядьте на колени и опустите таз на пятки. Руки разведите в стороны и согните их в локтях, а затем поднимите вверх. Ладони находятся в вертикальном положении и направлены вперед. Теперь опустите предплечья до параллели с полом, направляя ладони вниз. Поворачивайте руки в локтевых суставах, не задействуя плечевые. Еще одно эффективное упражнение на руки в домашних условиях, которое прорабатывает бицепсы и укрепляет внутреннюю сторону рук.", time = 40, category = "Для рук"))
        trainings.add(Training(image = R.drawable.icon_arms_10, title = "Упражнение №10", subtitle = "Подъем рук со сгибанием в планке на коленях", description = "Встаньте в планку на прямых руках и опустите колени вниз. Из этого положения поднимите правую руку и выпрямите ее, а затем заведите немного назад, сгибая ее в локте, после чего опустите вниз. Сгибая руку в локте, поднимайте его до параллели плеча с полом. Повторите левой рукой. Включайте упражнение в тренировку для рук в домашних условиях, чтобы проработать бицепсы, трицепсы, а также мышцы предплечий.", time = 30, category = "Для рук"))
        trainings.add(Training(image = R.drawable.icon_slim_1, title = "Упражнение №1", subtitle = "Джампинг джек с махами перед собой", description = "Примите положение обычной стойки, стопы расположите рядом и выпрямитесь от пяток до макушки по струнке. Руки опустите свободно вниз, ладони держите на передних поверхностях бедер. Смотрите вперед. Потом подпрыгните вверх, разведите пошире ноги в стороны. Одновременно с этим поднимите руки прямо перед собой до уровня плеч. Прыжком вернитесь в исходную позу. Будет лучше выполнять переходы почти без пауз, темп чуть выше среднего. Работают мышцы плечевого пояса, спины, ягодиц и ног, чувствуется кардио-эффект.", time = 45, category = "Для похудения"))
        trainings.add(Training(image = R.drawable.icon_slim_2, title = "Упражнение №2", subtitle = "Быстрые приседы с касаниями стоп", description = "Перейдите в стойку с раздвинутыми стопами на полторы ширины плеч, как при обычных приседаниях. Носки немного разверните, руки держите перед собой на уровне груди. Выполните отведение таза назад-вниз в присед – бедра доводятся до параллели пола, а колени остаются над стопами. Одновременно коснитесь до левой стопы пальцами правой руки, протянув ее между ногами. Обратно встаньте и повторите присед, поменяв стороны. При таком упражнении для похудения дома включаются мышцы всего тела, но акцент идет на ягодицы и бедра.", time = 35, category = "Для похудения"))
        trainings.add(Training(image = R.drawable.icon_slim_3, title = "Упражнение №3", subtitle = "Горизонтальный бег", description = "Разместитесь в планку на прямых руках – стопы поставьте вместе, ладони четко под плечи, живот с тазом подберите, спину держите ровно. Не меняйте позиции корпуса, подтяните правую ногу к себе, согнув в коленном суставе. Дотяните из исходного положения бедро до живота. Затем поставьте обратно, выполните тут же подтяжку левой ногой, снова уберите назад. Спину не округляйте. Не переводите туловище из стороны в сторону. Выполняйте такие подтягивания по беговой технике, не делая пауз между сменой ног. Помогает упражнение при устранении лишнего веса, придает тонус фигуре, укрепляет мышцы пресса.", time = 30, category = "Для похудения"))
        trainings.add(Training(image = R.drawable.icon_slim_4, title = "Упражнение №4", subtitle = "Прыжок-ножницы + колено-локоть", description = "Расположитесь в стойке, отодвинув ноги на полторы или две ширины плеч друг от друга, стопы на параллельных линиях. Руки уберите за голову, локти в стороны. Выпрямитесь в спине, смотрите вперед. Теперь прыгните, скрестите ноги, правую перед левой. Прыжком встаньте назад, левой коленкой дотянитесь до правого локтя, сделав скручивание корпуса. После этой комбинации повторите ножницы, заведя левую ногу перед правой, коснитесь из положения стоя левым локтем до правого колена. Пульс разгоняется, а расходы калорий растут, что и нужно в кардио для похудения в домашних условиях.", time = 40, category = "Для похудения", isRecommended = true))
        trainings.add(Training(image = R.drawable.icon_slim_5, title = "Упражнение №5", subtitle = "Прыжки на месте вперед-назад", description = "Сначала просто встаньте, стопы рядом, спина прямая, руки опущены. Теперь из этой позиции перейдите в исходное положение. Шагните вперед левой ногой на небольшое расстояние, слегка согните ее в коленном суставе, а руки разместите как при пробежке вдоль боков, разноименно к ногам. Подпрыгните и в воздухе перемените стороны. Должно оказаться так: вперед выставите правую ногу, при этом левую уберите назад. Чередуйте стороны между собой. Работайте в такт руками и смену делайте без пауз. Упражнение тонизирует, повышает пульс, расход калорий.", time = 50, category = "Для похудения"))
        trainings.add(Training(image = R.drawable.icon_slim_6, title = "Упражнение №6", subtitle = "Прыжок в сумо-присед с разведением рук", description = "Встаньте ровно, стопы расположите близко друг к другу, выпрямите спину. При этом руки поднимите вверх над головой, ладони вместе, локти чуть согнуты. Из приятой стойки немного подпрыгните, расставив широко ноги, развернув носки по сторонам. Опуститесь в присед-сумо, бедра до параллели пола, колени четко идут по направлению стоп. Руки переведите вниз кругом через стороны, ладони оставьте на уровне бедер. Подпрыжкой вернитесь в изначальную стойку. Потом повторите еще раз. Действенное упражнение для похудения дома, так как включаются сразу все мышцы, от плеч, рук и спины до каждой зоны бедер.", time = 35, category = "Для похудения"))
        trainings.add(Training(image = R.drawable.icon_slim_7, title = "Упражнение №7", subtitle = "Планка с ударами рукой вперед", description = "Опуститесь на коврик в планку на прямых руках, стопы расставьте подальше на единой линии друг от друга, ладони четко под плечами. Подберите живот. Если стоять в такой планке сложно, то можете переместиться на колени. Спина прямая, таз не выводите вверх. Поднимите правую руку, сделав удар вперед. Это должно произойти на линии туловища. Опустите на место. Немного переведите вес на другую сторону, повторите подъем-удар левой рукой. Держите корпус на всем протяжении зафиксированным. Достаточно сложное упражнение, грузятся мышцы пресса, спины и кора, интенсивно работает плечевой пояс с руками. Поднимается пульс, ускоряется жиросжигание.", time = 30, category = "Для похудения"))
        trainings.add(Training(image = R.drawable.icon_slim_8, title = "Упражнение №8", subtitle = "Подъемы ног в позе стола", description = "Перевернитесь в позу стола. Сначала сядьте, подогните ноги, руки поставьте позади ягодиц пальцами к себе. Затем слегка оторвите таз от пола. Кисти держите на уровне под плечами, а стопы под коленями. Локти выпрямлены, спина ровная. Начните поочередно поднимать ноги и делать на каждый из подъемов удар, распрямляя колени. Держитесь умеренной скорости, ставьте одну ногу и только потом поднимайте другую. Для завершающего упражнения нашего кардио для похудения в домашних условиях будет достаточно. Подъемы ног в позе стола укрепят все мышцы тела, активизируют жиросжигание.", time = 40, category = "Для похудения", isRecommended = true))
        // for man
        trainings.add(Training(image = R.drawable.icon_legs_m_1, title = "Упражнение №1", subtitle = "Приседания с гантелями на плечах", description = "Встаньте ровно, расставьте стопы пошире, на удобное расстояние для приседов классической техникой. Держите по единой линии спину, смотрите вперед. Возьмите в кисти по гантели, согните руки, чтоб положить снаряды на плечи. Сделайте приседание: опустите таз вниз-назад и до параллели пола доведите бедра. Колени не сваливайте внутрь, не выводите за носки. Поднимитесь, повторите. Работают мышцы всего низа тела: ягодицы, бицепсы, квадрицепсы, а также поясничная зона, пресс.", time = 30, category = "Для ног", gender = MALE))
        trainings.add(Training(image = R.drawable.icon_legs_m_2, title = "Упражнение №2", subtitle = "Кубковые приседания с гантелью", description = "Положение для приседаний сохраните, стопы шире плеч, носки слегка повернуты в стороны. Спина ровная, взгляд вперед. Обеими руками возьмите за один конец гантель. Согнув локти, поднимите снаряд перед собой и зафиксируйте гантель на уровне верха груди. Выполните присед из принятого положения: таз опустите вниз, бедра до параллели, колени над носками. Сильно корпус вперед не наклоняйте. Встаньте с упором в пятки, повторите. Такое упражнение с гантелями для ног нагружает квадрицепсы и ягодицы.", time = 30, category = "Для ног", gender = MALE))
        trainings.add(Training(image = R.drawable.icon_legs_m_3, title = "Упражнение №3", subtitle = "Приседания-сумо с гантелями", description = "Немного шире поставьте стопы, носки оставьте развернутыми. Держите прямой спину, смотрите четко вперед. В каждую кисть возьмите по гантели. Далее руки опустите вниз перед собой по направлению между ног, сомкните кулаки. Потом начните выполнять основное движение: опустите таз назад-вниз и остановитесь бедрами на параллельной полу линии. Наклоните немного корпус. Не выводите колени за стопы, не сваливайте их внутрь. Снаряды должны идти четко вниз. Упор сделайте на пятки, поднимитесь. Присед-сумо нагрузит мышцы внутри бедер и ягодицы, работают также квадрицепсы и бицепсы бедер.", time = 30, category = "Для ног", gender = MALE))
        trainings.add(Training(image = R.drawable.icon_legs_m_4, title = "Упражнение №4", subtitle = "Классические выпады с гантелями", description = "Примите положение обычной стойки, стопы разместите на ширине плеч, спина держится по прямой линии. Возьмите в каждую кисть по гантели, руки опущены ровно вдоль корпуса. Теперь перейдите к основному движению – сделайте шаг вперед левой ногой, стопу полностью поставьте, опустите таз в выпад, согнув колено. Доведя бедро до параллельной линии к полу, оттолкнитесь, встаньте на место. Держите корпус ровным, не наклоняйтесь. Выполните шаг левой ногой, чередуйте обе ноги. Выпады дают акцент на квадрицепсы с ягодицами, прорисовывается рельеф мышц.", time = 40, category = "Для ног", gender = MALE))
        trainings.add(Training(image = R.drawable.icon_legs_m_5, title = "Упражнение №5", subtitle = "Выпады на месте с гантелями", description = "Сохраните стойку из предыдущего упражнения. Прямо держите спину, верх корпуса расправьте. В кисти возьмите гантели и после опустите руки свободно вниз вдоль тела. Шагните левой ногой широко назад, поставьте на носок, перейдите в позу выпада. В правом колене угол 90 градусов, не нужно выводить его за стопу. Спина остается ровной, не наклоняйте ее вперед. Как бедро станет параллельно полу, поднимитесь и снова опуститесь. Поменяйте ноги. Такое упражнение с гантелями для мужчин включает в работу все мышцы ног и ягодиц.", time = 40, category = "Для ног", gender = MALE))
        trainings.add(Training(image = R.drawable.icon_legs_m_6, title = "Упражнение №6", subtitle = "Румынская тяга на одной ноге с гантелью", description = "Поставьте рядом стул или другую опору. В левую руку возьмите одну гантель, спина ровная, вес тела перенесите на правую ногу. Начните наклонять корпус вперед, одновременно отводя левую ногу назад. В нижней точке гантель почти касается пола, а левая нога поднимается до параллельного полу положения. Опирайтесь на стул, чтобы сохранять равновесие на одной ноге. В нижней точке спину держите прямой без сутулости, в пояснице сохраняйте естественный прогиб. Медленно вернитесь в исходное положение и повторите на ту же ногу. В этом упражнении с гантелями для мужчин на максимум нагружаются ягодицы и бицепсы бедер, работают поясничные мышцы и стабилизаторы.", time = 40, category = "Для ног", gender = MALE))
        trainings.add(Training(image = R.drawable.icon_legs_m_7, title = "Упражнение №7", subtitle = "Обратные выпады с гантелями", description = "Вернитесь в позицию для выпадов, расположите стопы на ширине плеч, возьмите по гантели в каждую ладонь и опустите руки свободно вдоль корпуса. Спина будет ровная, взгляд направлен вперед. Шагните левой ногой широко назад, поставьте носок на пол, правую согните в колене под прямым углом и не выводите за стопу. До параллели доведите бедро, примите позу выпада. Не наклоняйте спину. Упритесь в пятку, поднимитесь и шагните другой ногой. Работают квадрицепс и бицепс бедра, ягодицы. Упражнение с гантелями для ног эффектно оформит мышцы.", time = 40, category = "Для ног", gender = MALE))
        trainings.add(Training(image = R.drawable.icon_legs_m_8, title = "Упражнение №8", subtitle = "Зашагивания на скамью с гантелями", description = "Возьмите опору, например, скамью или стул. Затем встаньте к ней лицом, оставив стопы на близком расстоянии. Спина ровная, руки с гантелями опущены вдоль туловища. Начните выполнять зашагивания: поставьте на возвышенность полностью одну стопу, оттолкнитесь, поднимитесь, подставив рядом другую. Выпрямитесь, сойдите с опоры. Повторите, сменив ногу. На всей протяженности корпус остается прямым. Задействуется весь низ тела, дополнительно будет кардио-эффект. Считается упражнение с гантелями для ног одним из лучших на занятиях для мужчин, так как увеличивает бедра, прорабатывает квадрицепсы.", time = 40, category = "Для ног", gender = MALE))
        trainings.add(Training(image = R.drawable.icon_legs_m_9, title = "Упражнение №9", subtitle = "Подъемы на носки с платформой", description = "Встаньте перед низкой платформой или диском от штанги (можно выполнять упражнение без платформы, просто поднимаясь на носки на месте, но эффективность будет ниже). В обе ладони возьмите по гантели, опустите свободно руки вдоль туловища. Теперь поставьте стопы на край опоры, пятки касаются пола. Колени выпрямлены, спина ровная, взгляд вперед. Выполняйте после принятия этой позиции подъемы на носки, затем опускайтесь вниз ниже опоры. Тогда растяжение мышц будет ощутимее. Можно делать с одной гантелью, держаться свободной рукой за стену или что-то другое. Эффективно прорабатывается зона голеней, в ряду обязательных упражнений с гантелями для мужчин на ноги.", time = 30, category = "Для ног", gender = MALE))
        trainings.add(Training(image = R.drawable.icon_legs_m_10, title = "Упражнение №10", subtitle = "Ягодичный мостик с гантелью", description = "Прилягте на спину, плотно прижмитесь к полу всем корпусом. Далее подогните под себя ноги, поставив стопы на ширине плеч поближе к ягодицам. Поместите гантель на область тазовых костей внизу живота, держите снаряд руками. Такое положения будет исходным. Сделайте ягодичный мост, для чего поднимите таз до единой линии от корпуса до колен. Задержитесь на секунду, затем опуститесь. Умеренная скорость, никаких резких движений. В упражнении с гантелями для ног особый акцент идет на ягодицы с бицепсом бедра, менее активно работает поясница. На мостике нет осевой нагрузки, можно выполнять при проблемах со спиной.", time = 30, category = "Для ног", gender = MALE))
        trainings.add(Training(image = R.drawable.icon_shoulders_m_1, title = "Упражнение №1", subtitle = "Подъем гантелей перед собой", description = "Взяв снаряды, удерживайте их перед собой на уровне пояса. Руки должны быть немного согнуты в локте, чтобы исключить избыточную нагрузку с локтевых суставов. Далее на выдохе поднимите правую руку перед собой, фиксируя ее на уровне параллели с полом. На вдохе приведите конечность обратно, после чего сделайте аналогичное движение левой рукой. В ходе выполнения не поднимайте плечи, чтобы трапеции не забирали основную нагрузку. Движения должны осуществляться аккуратно и без рывков. Представленное упражнение на руки для мужчин акцентирует напряжение в передних пучках дельтоидов, полностью изолируя их от других частей мышечной группы.", time = 40, category = "Для плеч", gender = MALE))
        trainings.add(Training(image = R.drawable.icon_shoulders_m_2, title = "Упражнение №2", subtitle = "Разведение гантелей в стороны", description = "В исходном положении удерживайте снаряды перед собой на уровне пояса параллельным хватом. Работайте без рывков и резких движений, дабы избежать риска получения травмы. Плечи должны быть опущены, дабы снять напряжение с трапециевидных мышц. На выдохе разведите руки в стороны так, чтобы тело приняло Т-образную форму. В пиковой точке немного проверните кисти, представляя, будто опустошаете содержимое двух кувшинов. Это необходимо для концентрации нагрузки в целевых мышечных группах. Таковыми в конкретном случае являются средние пучки дельтоидов, которые формируют общую ширину плеч, а также эстетичную форму верхних конечностей.", time = 40, category = "Для плеч", gender = MALE))
        trainings.add(Training(image = R.drawable.icon_shoulders_m_3, title = "Упражнение №3", subtitle = "Жим гантели одной рукой", description = "Ноги расставьте на ширине плеч, левую руку направьте в сторону, а в правой удерживайте снаряд на уровне ниже подбородка. Ладонь развернута внешней стороной от себя. На выдохе выжмите вес над собой и поверните кисть так, чтобы вперед смотрела уже внутренняя сторона ладони. При подъеме не допускайте полного раскрытия локтевого суставного замка. На вдохе опустите вес обратно, после чего проделайте необходимое число повторений (не забудьте про другую руку на следующий подход). В ходе работы поддерживайте прямую осанку и не допускайте перекосов туловища из стороны в сторону. Такое упражнение на руки с гантелями отличается тем, что развивает «взрывную» силу рук, делает дельты объемнее, благотворно воздействует на трицепс.", time = 40, category = "Для плеч", gender = MALE))
        trainings.add(Training(image = R.drawable.icon_shoulders_m_4, title = "Упражнение №4", subtitle = "Разведение гантелей в наклоне", description = "Взяв гантели в руки, наклоните корпус вперед, поддерживая при этом естественный прогиб в спине. Ноги находятся в слегка согнутом положении, руки выпрямлены к полу. На выдохе выполните разведение снарядов в стороны, направляя локти вверх к потолку. Подъем совершайте до уровня, пока руки не окажутся горизонтальными полу. После небольшой паузы верните верхние конечности в стартовое положение и зациклите движение в среднем темпе. Такое упражнение на руки для мужчин полностью изолирует задний пучок дельтоидов, укрепляет поясницу и околопозвоночные столбы, помогает сформировать атлетичную форму широчайшей мышцы спины.", time = 40, category = "Для плеч", gender = MALE))
        trainings.add(Training(image = R.drawable.icon_shoulders_m_5, title = "Упражнение №5", subtitle = "Тяга гантелей к подбородку", description = "Удерживайте снаряды перед собой продольным хватом (для удобства представьте, будто в руках находится гриф штанги). Ноги расставьте на ширине плеч, выпрямите спину. На выдохе поднимите вес к подбородку так, чтобы траектория движения гантелей была вдоль туловища. Локти при этом двигаются в противоположные друг от друга стороны. Выполняя упражнение, не поднимайте плечи, дабы исключить напряжение с трапеций. Элемент силовой тренировки на руки с гантелями направлен на развитие средних пучков дельт, развитие их объема, укрепление кистевого хвата, а также силы бицепсов.", time = 40, category = "Для плеч", gender = MALE))
        trainings.add(Training(image = R.drawable.icon_triceps_m_1, title = "Упражнение №1", subtitle = "Разгибание руки из-за головы", description = "Можно выполнять упражнение стоя или сидя. Возьмите гантель в правую руку. После этого поднимите вес над собой до уровня, пока рука не окажется перпендикулярна полу. Левой рукой слегка придерживаем правую в области трицепса для лучшей стабилизации и снижения нагрузка с локтевого сустава. Медленно опускаем снаряд за голову до уровня, пока угол сгиба в локтевом суставе не окажется прямым. После этого разгибаем руку, не допуская полного закрытия суставного замка в пиковой точке.\n" +
                "\n" +
                "Проделав нужное количество повторений, меняем руку и работаем по аналогичной амплитуде. В ходе работы следите, чтобы корпус не перекашивался и не «вилял» из стороны в сторону. Представленное упражнение на трицепс для мужчин обеспечивает изолированную проработку трехглавой мышцы, увеличивая ее качественный объем и рельеф.", time = 40, category = "Для трицепса", gender = MALE))
        trainings.add(Training(image = R.drawable.icon_triceps_m_2, title = "Упражнение №2", subtitle = "Разгибание рук в наклоне", description = "Работаем из положения стоя. Возьмите гантели в обе руки, после чего сделайте наклон корпусом вперед до уровня чуть выше параллели полу. Внимательно проследите за тем, чтобы в спине поддерживался естественный прогиб, а плечи были расправлены. Прижимаем локти к боковым частям корпуса. Изначально руки находятся в согнутом положении, предплечья в исходной позиции перпендикулярны полу.\n" +
                "\n" +
                "Далее на выдохе выполняем разгибание рук так, чтобы в пиковой точке они оказались параллельными поверхности пола. Полностью закрывать суставной локтевой замок не нужно с целью исключения его перенапряжения. Выдержав небольшую паузу, возвращаемся в исходную позицию и зацикливаем упражнение. Элемент тренировки трицепса для мужчин опять же направлен на развитие объема и рельефа трехглавой мышцы.", time = 40, category = "Для трицепса", gender = MALE))
        trainings.add(Training(image = R.drawable.icon_triceps_m_3, title = "Упражнение №3", subtitle = "Алмазные отжимания", description = "Принимаем упор лежа таким образом, чтобы раскрытые ладони были зафиксированы практически вплотную друг к другу. Большие и указательные пальцы стыкуются друг с другом, образуя тем самым своеобразную ромбовидную форму, в честь чего упражнение было названо «алмазным». Вы можете упростить упражнение, если будете выполнять его на коленях или с опорой на скамью.\n" +
                "\n" +
                "Для отжимания опускаем вес тела, направляя локти вдоль корпуса, но не прижимая их. Грудь должна остановиться на расстоянии пары сантиметров от пола (ложиться полностью не нужно). Теперь с усилием отталкиваемся, возвращаясь в исходное положение. Опять же полностью разгибать руки не нужно, поддерживая небольшой угол. Помимо нагрузки на трехглавую мышцу, элемент тренировки трицепса в домашних условиях обеспечивает качественную проработку средней части груди и в целом развивает мускулатуру верхней половины тела мужчины.", time = 40, category = "Для трицепса", gender = MALE))
        trainings.add(Training(image = R.drawable.icon_triceps_m_4, title = "Упражнение №4", subtitle = "Разгибания рук из-за головы с гантелей", description = "Можно выполнять упражнение стоя или сидя. Возьмите гантель за боковые стороны обеими руками, после чего поднимите их над головой. На выдохе аккуратно сгибаем руки за голову до уровня, пока угол в локтевом суставе не окажется прямым. На вдохе разгибаем руки, не допуская полного закрытия суставного замка.\n" +
                "\n" +
                "В процессе работы поддерживайте ровную осанку, не сутультесь и держите плечи расправленными, не допуская их поднятия (перегрузка трапеции в данном случае нам не нужна). Упражнения на трицепс для мужчин в данном случае обеспечивает одновременное симметрическое развитие целевой мышечной группы, придавая трехглавым мышцам нужный рельеф.", time = 40, category = "Для трицепса", gender = MALE))
        trainings.add(Training(image = R.drawable.icon_triceps_m_5, title = "Упражнение №5", subtitle = "Поочередное разгибание рук в наклоне", description = "Берем гантели в руки и наклоняем корпус вперед. В спине должен поддерживаться естественный прогиб, ни в коем случае не допускайте прогиба позвоночника «колесом». Расправьте плечи и вытяните руки вдоль туловища параллельно полу. Теперь согните левую руку так, чтобы предплечье оказалось перпендикулярным полу. Следующим движением разогните левую руку, одновременно сгибая правую по аналогичной амплитуде.\n" +
                "\n" +
                "На каждое повторение чередуем сгибание и выпрямление конечностей. Для удобства можете представить, будто отталкиваетесь лыжными палками. Элемент тренировки трицепса в домашних условиях является вариацией упражнения №2, но с более акцентированной нагрузкой на каждую руку.", time = 35, category = "Для трицепса", gender = MALE))
        trainings.add(Training(image = R.drawable.icon_triceps_m_6, title = "Упражнение №6", subtitle = "Отжимания до упора", description = "Принимаем классический упор лежа, после чего полностью лягте на грудь и оторвите ладони от поверхности пола. Ноги стандартно зафиксированы на носках. Предплечья должны быть перпендикулярны линии корпуса. Теперь упираемся ладонями в пол и с усилием отталкиваемся, выполняя стандартное отжимание. Движение должно получиться резким и уверенным. После этого вновь вернитесь в стартовую позицию и проделайте упражнение на запланированное количество раз.\n" +
                "\n" +
                "Помимо развития трехглавой мышцы представленный элемент тренировки трицепса для мужчин развивает грудную мускулатуру, а также «взрывную» мощь рук, что очень важно для людей, занимающихся активными видами спорта.", time = 40, category = "Для трицепса", gender = MALE))
        trainings.add(Training(image = R.drawable.icon_triceps_m_7, title = "Упражнение №7", subtitle = "Подъем-разгибание руки в планке с гантелью", description = "Вновь работаем из упора лежа, только в данном случае одна рука упирается в гриф предварительно взятой гантели. Туловище удерживается на вытянутых руках. Линия шеи, спины и ног должна быть прямой без видимых изгибов. Первым движением выполняем подъем гантели, направляя локоть к потолку. После этого разгибаем конечность до параллельного полу положения, после чего вновь сгибаем ее и ставим в исходную позицию.\n" +
                "\n" +
                "Закончив запланированное число повторов, смените сторону и работайте другой рукой. Упражнение на трицепс для мужчин дополнительно обеспечивает увеличение статической силы рук, прорабатывает широчайшую мышцу спины, а также укрепляет мышцы-стабилизаторы. Отличное функциональное упражнение для мужчин.", time = 40, category = "Для трицепса", gender = MALE))
        trainings.add(Training(image = R.drawable.icon_triceps_m_8, title = "Упражнение №8", subtitle = "Разгибание рук лежа на спине", description = "Лягте на спину и подогните ноги, зафиксировав их на полной стопе. Возьмите в руки гантели, после чего выжмите вес перед собой. В исходном положении верхние конечности должны находиться в перпендикулярном корпусу положении. На выдохе выполняем сгибание рук до прямого угла в сгибе локтевых суставов. В пиковой точке гантели должны находится по боковым сторонам головы. После этого возвращаемся в исходную позицию, аккуратно разгибая конечности.\n" +
                "\n" +
                "Зацикливаем движение, стараясь поддерживать средний темп выполнения. Это эффективное и безопасное упражнение из тренировки трицепса для мужчин, который обеспечивает полную изоляцию целевой мышечной группы, способствуя ее симметричному росту.", time = 40, category = "Для трицепса", gender = MALE))
        trainings.add(Training(image = R.drawable.icon_triceps_m_9, title = "Упражнение №9", subtitle = "Обратные отжимания от скамьи", description = "Сядьте на стул, диван или скамью, после чего упритесь ладонями в поверхность. Сделайте шаг вперед и удерживайте вес туловища за счет силы верхних конечностей. Вытяните ноги и зафиксируйте их на пятке. Далее на выдохе медленно опускаемся вниз до уровня, пока руки не будут согнуты под углом в 90 градусов. Сделав небольшую паузу, с усилием выталкиваемся обратно, не допуская при этом полного защелкивания суставного замка.\n" +
                "\n" +
                "На протяжении всей амплитуды движения старайтесь поддерживать ровную осанку. Не поднимайте плечи, чтобы избежать лишней нагрузки на трапециевидные мышцы. Обратные отжимания – эффективный элемент для проработки трицепсов без дополнительных снарядов, выполнять которое могут все атлеты без исключения. Вы можете упростить упражнение, если согнете ноги в коленях.", time = 40, category = "Для трицепса", gender = MALE))
        trainings.add(Training(image = R.drawable.icon_triceps_m_10, title = "Упражнение №10", subtitle = "Разгибания рук в планке на локтях", description = "Примите положение классической планки с опорой на предплечья. Шея, спина и ноги образуют сплошную ровную линию. Специфика упражнения заключается в поочередном выпрямлении рук вдоль корпуса с небольшими паузами в пиковых точках. Сначала работайте левой рукой, а затем правой, поддерживая средний темп выполнения.\n" +
                "\n" +
                "На протяжении всего подхода следите за тем, чтобы спина не горбилась и не провисала к низу. Помимо мощного эффекта на трицепсы, упражнение в целом нагружает все тело, способствуя укреплению мышц кора, а также развитию стабилизаторов.", time = 35, category = "Для трицепса", gender = MALE))
        trainings.add(Training(image = R.drawable.icon_body_m_1, title = "Упражнение №1", subtitle = "Приседания + выпады назад", description = "В исходной позиции руки сведены в замке на уровне груди. Ноги расставлены чуть шире плеч, спина удерживается в ровном положении, взгляд направлен перед собой. Делаем присед до прямого угла в сгибе коленных суставов, не допуская выхода колена за линию носков. С усилием поднимаемся, после чего переносим вес на левую ногу и делаем обратный выпад, отводя назад правую. Поднимаемся, снова приседаем и переносим вес уже на правую ногу, делая аналогичный выпад назад. Элемент тренировки для мужчин с собственным весом обеспечивает комплексную нагрузку на ноги, позволяя увеличить объем нижних конечностей, их выносливость и физическую силу.", time = 40, category = "Для всего тела", gender = MALE))
        trainings.add(Training(image = R.drawable.icon_body_m_2, title = "Упражнение №2", subtitle = "Подброс бедра", description = "По-прежнему удерживаем руки в замке на уровне груди. Переносим вес тела на правую ногу, а левую отводим назад. Корпус при этом немного наклонен вперед. В спине поддерживается естественный прогиб. Ваша задача – совершать поочередные подъемы колена к низу груди, стараясь работать в высоком темпе. Рабочая нога в процессе отвода вытягивается полностью, образуя со спиной ровную линию. При подъеме делаем выдох, а при возврате – вдох. Упражнение эффективно задействует нижнюю часть пресса, способствуя сжиганию жира в области пояса у мужчин. Дополнительно подброс бедра изолирует квадрицепс, помогая добиться рельефных ног.", time = 50, category = "Для всего тела", gender = MALE))
        trainings.add(Training(image = R.drawable.icon_body_m_3, title = "Упражнение №3", subtitle = "Отжимания с касанием рукой плеча", description = "Принимаем упор лежа, расставляя ладони на уровне чуть шире плеч. Они должны находиться строго параллельно друг другу, поскольку это позволит избежать неправильной траектории движения. Согните локти, сделайте отжимание, опускаясь грудью к полу. Необходимо зафиксировать положение на расстоянии 4-5 см от его поверхности. Далее с усилием выталкиваемся, не допуская полного закрытия локтевого сустава в исходной фазе. После этого правой ладонью касаемся левого плеча. Затем снова отжимаемся, только на подъеме касаемся уже левой ладонью правого плеча. Упражнение для мужчин с собственным весом обеспечивает отличную нагрузку на грудь, дельты, трицепсы, а также мышцы кора.", time = 30, category = "Для всего тела", gender = MALE))
        trainings.add(Training(image = R.drawable.icon_body_m_4, title = "Упражнение №4", subtitle = "Подтягивание коленей в планке", description = "Находясь в положении упора лежа, немного сведите руки друг к другу, поставив ладони на одну линию с плечами. Техника упражнения достаточно проста. Вам необходимо поочередно подтягивать колени к уровню груди, фиксируя каждое движение небольшой паузой в пиковой точке. Это необходимо для усиления акцента напряжения в целевых группах мышц. Старайтесь работать в среднем темпе и ни в коем случае не делайте резких движений. Руки, спина и мышцы кора выполняют роль стабилизаторов, интенсивно работает пресс и квадрицепсы. Это одно из лучших упражнений для избавления от живота мужчинам.", time = 40, category = "Для всего тела", gender = MALE))
        trainings.add(Training(image = R.drawable.icon_body_m_5, title = "Упражнение №5", subtitle = "\"Охотничья собака\"", description = "Исходная позиция – стоя на четвереньках. Спина не должна горбиться или прогибаться к полу, держите ровное положение. Взгляд направьте четко перед собой. После этого на выдохе поднимите правую ногу и левую руку так, чтобы они образовали одну сплошную линию с шеей и спиной. Зафиксируйте положение на секунду, после чего вернитесь в исходную фазу. Далее работаем со сменой конечностей (поднимаем уже левую ногу и правую руку), соблюдая аналогичную траекторию движения. Элемент тренировки для мужчин с собственным весом помогает восстановить дыхание и пульс, одновременно нагружая бицепсы бедер, передние пучки дельт, а также стабилизируя пресс и мускулатуру поясницы. Кроме того, это отличное упражнение для исправления осанки, которая является слабым звеном для многих людей.", time = 40, category = "Для всего тела", gender = MALE))
        trainings.add(Training(image = R.drawable.icon_body_m_6, title = "Упражнение №6", subtitle = "Выпады вперед", description = "Встаньте, поставьте ноги ближе друг к другу, а руки зафиксируйте на поясе. После этого сделайте широкий шаг вперед, после чего опустите вес тела вниз. Впереди стоящая нога сгибается до прямого угла в колене (не ниже), дабы избежать чрезмерной нагрузки на сустав. Колено стоящей сзади ноги не должно касаться пола, оставаясь на расстоянии пары сантиметров от его поверхности в пиковой точке. В ходе работы внимательно следите, чтобы стопы находились приблизительно на одной линии. Данное упражнение для мужчин с собственным весом отлично развивает объем мускулатуры ног и ягодиц, являясь отличной альтернативой приседаниям.", time = 40, category = "Для всего тела", gender = MALE))
        trainings.add(Training(image = R.drawable.icon_body_m_7, title = "Упражнение №7", subtitle = "Приседания с вытягиванием рук", description = "В целом упражнение достаточно похоже на обычные приседания, только предполагает более высокий темп и включение в работу мышц рук. Ноги стоят на ширине плеч, а сед выполняется до прямого угла в сгибе коленей, которые не должны выходить за линию носков в нижней точке. Основное различие от обычных приседаний заключается в том, что мы будто готовимся выпрыгнуть из нижней позиции, выполняя инерционное отталкивающее движение руками. Подъем выполняется с усилием, но без прыжка. Одновременно с этим поднимаем руки вверх, после чего вновь приступаем к очередному повторению. Представленный элемент направлен на развитие взрывной силы ног, дополнительно включаются в работу дельтовидные мышцы.", time = 35, category = "Для всего тела", gender = MALE))
        trainings.add(Training(image = R.drawable.icon_body_m_8, title = "Упражнение №8", subtitle = "Переход из планки в упор лежа", description = "Принимаем классическое положение планки. Руки опираются на предплечья, стоящие параллельно друг другу. Спина удерживается в ровном положении, не сутулится и не провисает к полу. Ноги зафиксированы на носках и стоят вплотную друг к другу. После этого вам необходимо поставить правую руку на полную ладонь, а затем и левую. Таким образом, мы принимаем стандартный упор лежа и фиксируем положение. После этого вновь переходим в планку. Не забываем чередовать подъемы с правой и с левой руки для равномерного распределения нагрузки. Элемент тренировки для мужчин с собственным весом оказывает колоссальный эффект на мышцы рук, развивая их силу, выносливость и объем.", time = 40, category = "Для всего тела", gender = MALE))
        trainings.add(Training(image = R.drawable.icon_body_m_9, title = "Упражнение №9", subtitle = "Скручивания корпуса с поднятыми ногами", description = "Ложимся на спину и подкладываем руки за голову. Ладонь нужно зафиксировать на другой ладони, а локти развернуть в противоположные стороны. Ноги согните в коленях на 90 градусов, после чего поднимите их так, чтобы бедра встали перпендикулярно полу. Это начальная позиция. Специфика упражнения заключается в выполнении подъемов корпуса по типу скручиваний. Старайтесь работать верхней частью туловища, акцентируя напряжение в мышцах кора благодаря задержкам в пиковой точке. Благодаря данному упражнению для мужчин с собственным весом вы сможете проработать заветные 6 кубиков и подтянуть зону пояса.", time = 30, category = "Для всего тела", gender = MALE))
        trainings.add(Training(image = R.drawable.icon_body_m_10, title = "Упражнение №10", subtitle = "Велосипед", description = "Не меняя положения лежа на спине, вытяните руки вдоль корпуса и зафиксируйте на ладонях. После этого вытяните ноги и немного приподнимите их от пола. Голову поднимать не нужно, поэтому затылок прижимаем к полу и смотрим в потолок. После этого начинаем выполнять воображаемые вращения педалей, поочередно поднимая колени к уровню солнечного сплетения. Работайте в среднем темпе и не торопитесь, чтобы лучше проработать целевую группу мышц. Элемент сочетает в себе статичную и динамическую работу и отлично прокачивает мускулатуру живота без травмирующей нагрузки. Кроме того, такой несложный велосипед мягко массирует поясницу, что полезно для людей с сидячим образом жизни.", time = 40, category = "Для всего тела", gender = MALE))
        trainings.add(Training(image = R.drawable.icon_chest_m_1, title = "Упражнение №1", subtitle = "Жим гантелей на горизонтальной скамье", description = "Поставьте снаряды в зоне досягаемости рук. Начните с принятия правильного положения. Затылок, спина и таз прижаты к скамье без провисов. Пятки вдавлены в пол, ноги разведены на 60 градусов. Запомните положение, сядьте, возьмите гантели, и поставьте их на колени. Лягте, одновременно перенося снаряды на грудь. Разогните руки, оставляя небольшой угол в локтевом суставе, держа гантели параллельно, как гриф. Упражнение на грудь начните с плавного опускания веса, двигая локти вбок и вниз. Почувствовав растяжение грудных мышц, возвращайте руки в исходную позицию.", time = 30, category = "Для мышц груди", gender = MALE))
        trainings.add(Training(image = R.drawable.icon_chest_m_2, title = "Упражнение №2", subtitle = "Разведение рук с гантелями на горизонтальной скамье", description = "Сядьте на край скамьи, поставив снаряды на колени. Лягте на лавку, прижав затылок, спину и поясницу. Ноги стоят широко с упором на пятку. Взгляд направьте в потолок, вытяните руки, образовывая угол в 90 градусов с полом. Оставьте небольшой угол в локтевом суставе, ладони смотрят друг на друга. Аккуратно выполните разведение. Локти смотрят вниз. Движение совершается только суставом плеча! Почувствовав растяжение, еще немного опустите руки, и на выдохе верните в изначальную позицию.", time = 30, category = "Для мышц груди", gender = MALE))
        trainings.add(Training(image = R.drawable.icon_chest_m_3, title = "Упражнение №3", subtitle = "Жим гантелей лежа на скамье хватом «молоток»", description = "Упражнение на грудь начните с принятия правильного положения. Сядьте на край скамьи, поставив снаряды на колени. Ложитесь, плотно прижимая затылок, спину и поясницу к скамье. Одновременно перенесите вес с бедер к груди. Поднимите руки перед собой, соблюдая угол в 90 градусов к полу. Ладони повернуты друг к другу, локти немного согнуты. На вдохе сгибайте руки, приводя гантели к груди. На выдохе — разгибайте, акцентируя внимание на напряжении грудных мышц. Повтор завершен.", time = 30, category = "Для мышц груди", gender = MALE))
        trainings.add(Training(image = R.drawable.icon_chest_m_4, title = "Упражнение №4", subtitle = "Жим гантелей на наклонной скамье", description = "Установите наклон скамьи на 30 градусов, чтобы минимизировать работу дельт и трицепса. Сядьте на край, поставив гантели на бедра. Лягте на скамью, плотно прижав затылок, спину и поясницу. Одновременно перенесите вес к груди, соблюдая горизонтальное положение снарядов (представьте, что держите гриф). Аккуратно поднимите руки параллельно друг другу. После — опустите до ощущения растяжения в области груди. Локти при этом развернуты, направлены к стороне ушей. Верните гантели в исходное положение.", time = 30, category = "Для мышц груди", gender = MALE))
        trainings.add(Training(image = R.drawable.icon_chest_m_5, title = "Упражнение №5", subtitle = "Разведение рук с гантелями на наклонной скамье", description = "Установите угол скамьи на 30 градусов. Возьмите гантели, примите положение лежа, прижав затылок, спину и поясницу без провисов. Ступни уверенно стоят на полу. Руки выпрямлены над головой с небольшим углом в локтях. На выдохе плавно начинайте разведение, чтобы локти постоянно смотрели вниз, а лопатки сводились друг к другу. В нижней точки должно чувствоваться растяжение грудных мышц (без явной боли). Верните руки в начальную точку, достигая пика напряжения.", time = 30, category = "Для мышц груди", gender = MALE))
        trainings.add(Training(image = R.drawable.icon_chest_m_6, title = "Упражнение №6", subtitle = "Жим гантелей лежа на наклонной скамье хватом «молоток»", description = "Установите спинку скамьи под углом 30 градусов. Примите исходное положение, плотно прижавшись спиной к поверхности. Глаза смотрят прямо в потолок. Ноги плотно стоят на полу, гантели прижаты к груди, ладони смотрят друг на друга. На выдохе поднимите вес, держа руки параллельно друг другу. Оставьте небольшой угол в локте. На вдохе вернитесь в начальное положение. Повторите упражнение на грудь нужное количество раз.", time = 30, category = "Для мышц груди", gender = MALE))
        trainings.add(Training(image = R.drawable.icon_chest_m_7, title = "Упражнение №7", subtitle = "Жим гантелей на горизонтальной скамье вниз головой", description = "Установите отрицательный угол наклона в 30 градусов (подойдет специальная скамья для пресса). Попросите напарника помочь подать гантели, и подстраховать при выполнении. Лягте, зацепившись носками за валики. Плотно прижмитесь спиной к поверхности скамьи. Примите гантели, взяв к груди горизонтально. На выдохе выжимайте, чтобы в верхней точке снаряды плавно соприкоснулись. Локти остаются немного согнуты, снижая нагрузку с трицепса. Опускайте вес вниз до прикосновения к груди/плечам. Локтевые суставы при этом развернуты в стороны и вниз.", time = 30, category = "Для мышц груди", gender = MALE))
        trainings.add(Training(image = R.drawable.icon_chest_m_8, title = "Упражнение №8", subtitle = "Разведение гантелей вниз головой", description = "Установите отрицательный наклон скамьи в 30 градусов. Закрепите лодыжки под валиками, лягте на поверхность, плотно прижавшись спиной, затылком и поясницей. Попросите напарника подать гантели, после чего выжмите их над головой, закрепив небольшой угол в локтях. Ладони параллельны друг другу. Начинайте плавно разводить снаряды, удерживая локтевые суставы положением вниз. В нижней точке почувствуйте ощущение растяжения в груди. Далее с усилием, но без резких рывков сведите руки в изначальном положении.", time = 30, category = "Для мышц груди", gender = MALE))
        trainings.add(Training(image = R.drawable.icon_chest_m_9, title = "Упражнение №9", subtitle = "Жим гантелей на полу (для домашних условий)", description = "Примите положение лежа на спине, немного согнув ноги в коленях. Используйте коврик, дабы уменьшить жесткость поверхности. Возьмите гантели с небольшим весом, разведите руки, и согните в локтях на 90 градусов. Предплечья находятся в перпендикулярном полу положении. Выжмите вес, задержавшись на пару секунд в верхней амплитуде. Теперь опустите локти к полу, не снимая напряжение с груди.", time = 30, category = "Для мышц груди", gender = MALE))
        trainings.add(Training(image = R.drawable.icon_chest_m_10, title = "Упражнение №10", subtitle = "Разведение гантелей лежа на полу (для домашних условий)", description = "Примите положение лежа на специальном коврике для тренировок. Слегка согните ноги, прочно встав на стопу. Плотно прижмите поясницу, спину и голову. Возьмите гантели в руки, и выпрямьте перед собой, соблюдая небольшой угол в локтях. Взгляд направьте в потолок. На вдохе выполняйте разводку до касания локтями пола. С усилием, но без рывков верните руки в исходное положение.", time = 30, category = "Для мышц груди", gender = MALE))
        // for women
        trainings.add(Training(image = R.drawable.icon_triceps_w_1, title = "Упражнение №1", subtitle = "Разгибания одной руки из-за головы", description = "Встаньте ровно, разместите стопы на ширине плеч. Держите прямой спину. Для завершения исходного положения возьмите в правую кисть гантель, а затем поднимите ее над головой так, чтобы ладонь была повернута вперед. Теперь идет основное движение: согните руку в локте за себя, то есть к левому плечу, не отрывая ее от головы, далее обратно разогните и повторите еще раз. Плечевая кость всегда по ровной траектории смотрит вверх, можно поддерживать ее свободной ладонью. После целого подхода поменяйте руки и снова сделайте разгибания на другую руку. Нагрузятся трицепсы, улучшится тонус в руках, укрепятся мышцы без значимого увеличения объема.", time = 30, category = "Для трицепса", gender = FEMALE))
        trainings.add(Training(image = R.drawable.icon_triceps_w_2, title = "Упражнение №2", subtitle = "Разгибания рук назад стоя в наклоне", description = "Поставьте устойчиво стопы, распрямите спину. Возьмите по гантели на каждую руку, опустите их свободно вдоль туловища. Теперь наклонитесь, слегка согнув колени и немного отведя таз назад, корпус должен остановиться по линии чуть выше параллели пола. Поясница почти не прогибается. Подогните руки, снаряды держите около талии, локти окажутся над спиной. Выполните из принятого положения разгибания-сгибания руками. Все остальное тело остается статичным. Позиция локтей не меняется. Упражнение поможет подтянуть трицепсы женщине, также сделать их более рельефными, причем на разгибании работают все три головки.", time = 30, category = "Для трицепса", gender = FEMALE))
        trainings.add(Training(image = R.drawable.icon_triceps_w_3, title = "Упражнение №3", subtitle = "Отжимания с узкой постановкой от скамьи", description = "Примите положение для отжиманий от опоры. Можно взять для этого устойчивый стул, диван или кресло, если дома, или скамейку в зале. Ладони поставьте узкой постановкой на край, то есть четко по линиям плеч. Ноги стоят вместе, колени распрямлены, стопы на носках. От пяток до макушки единая плоскость. Корпус ровный, не провисает поясница, живот с тазом подобраны. Согните руки, чтобы локти прошли вдоль тела, опустите грудь на опору, поднимитесь назад. Повторите еще раз. Такая форма отжиманий акцентированно проработает трицепс, дополнительно включатся мышцы груди и плеч.", time = 25, category = "Для трицепса", gender = FEMALE))
        trainings.add(Training(image = R.drawable.icon_triceps_w_4, title = "Упражнение №4", subtitle = "Жим гантели из-за головы двумя руками", description = "Вернитесь в обычную стойку с устойчивыми стопами на ширине плеч. Затем возьмите горизонтально в руки гантель, поднимите ее над головой. Корпус остается ровным, таз не отводится назад. Одновременно из принятой позиции согните обе руки за голову и опустите на уровень, когда угол будет чуть меньше 90 градусов в локтях, причем не разводите их в стороны, для чего плотно прижмите руки к зоне ушей. Разогните назад без рывка, выполните жим еще раз. Упражнение используется в сушке трицепса для девушек, так как эффективно прорабатывает мускулатуру, особенно длинный пучок, убирает дряблость рук. Это упражнение также укрепит стабилизаторы, в том числе кор, пресс и спину.", time = 30, category = "Для трицепса", gender = FEMALE))
        trainings.add(Training(image = R.drawable.icon_triceps_w_5, title = "Упражнение №5", subtitle = "Поочередные разгибания рук в наклоне", description = "Снова вернитесь в положение наклона. Стопы удалите на ширину плеч, согните немного колени и отведите слегка таз назад, опустив корпус к параллели пола, а руки с гантелями вытяните свободно вниз. Затем выполните ими тягу к поясу за счет сгибания, снаряды окажутся на уровне живота, локти чуть выше спины. Не меняя позиции туловища, начните разгибать-сгибать поочередно руки. Пауз нет между сменами, темп движений немного ускоренный. Это упражнение рекомендуется включить в тренировки трицепса для женщин, так как повышается тонус мышц и кожи рук, работает эффект жиросжигания.", time = 40, category = "Для трицепса", gender = FEMALE))
        trainings.add(Training(image = R.drawable.icon_triceps_w_6, title = "Упражнение №6", subtitle = "Обратные отжимания для рук от опоры", description = "Встаньте ровно спиной к опоре, это может быть скамейка, стул или диван. Опустите вниз руки, установите ладони на край пальцами к себе. Шагами перенесите ноги немного вперед, поставьте на пятки, стопы вместе, колени согнуты. Плечи четко над кистями, в спине прямая линия, без прогибов или сутулости. Из этого положения сделайте обратные отжимания: согните руки, пока в локтях не будет угол 90 градусов, в это время таз с туловищем опускается вниз. После обратно разогните, вернув исходную позицию. Упражнение помогает подтянуть трицепс женщине за счет акцента на все пучки трехглавой мышцы, работают также плечи и грудь.", time = 30, category = "Для трицепса", gender = FEMALE))
        trainings.add(Training(image = R.drawable.icon_triceps_w_7, title = "Упражнение №7", subtitle = "Тяга гантели с разгибанием в планке", description = "Переместитесь в планку на прямых руках, держите в левой руке гантель. Правая ладонь на полу четко под плечами, а стопы на небольшом удалении друг от друга. Живот и таз подобраны, создана прямая линия корпуса, прогибов позвоночника нет. Теперь главное движение: оторвите с пола левую руку, вверх потянув локоть, доведите снаряд до боковой части груди, затем из этой позиции выполните разгибание назад, верните на исходное место. Выполняйте целый подход на одну руку. Отличное упражнение на трицепс и верх корпуса, группы мышц которого укрепляются, тонизируются, становятся более рельефными.", time = 40, category = "Для трицепса", gender = FEMALE))
        trainings.add(Training(image = R.drawable.icon_triceps_w_8, title = "Упражнение №8", subtitle = "Французский жим лежа на спине", description = "Прилягте на спину, подогните ноги на себя, поставьте стопы на ширине плеч по траектории напротив таза, корпус плотно прижмите к полу. Возьмите в каждую кисть по гантели и выпрямите руки над грудью вверх, ладони развернув друг на друга. Из принятой позиции выполните основное движение: согните в локтях за голову руки, пока снаряды не дойдут до уровня ушей, выжмите обратно и далее повторите еще раз. Туловище остается в статичном положении и не помогает из нижней точки поднимать гантели. В сушке трицепса для девушек это обязательное упражнение, так как прорабатывает трехглавые мышцы, подтягивает руки.", time = 30, category = "Для трицепса", gender = FEMALE))
        trainings.add(Training(image = R.drawable.icon_triceps_w_9, title = "Упражнение №9", subtitle = "Разгибания рук из планки на локтях", description = "Для выполнения этого элемента тренировки трицепса для девушек перейдите в позицию планки на предплечьях. Поставьте локти под плечи, на параллельных линиях уложите предплечья. Стопы на носках, спина прямая (можно выполнять на коленях). Таз подобран, живот напряжен. Оставьте опору на правой руке и оторвите левую от пола, разогните в локте назад вдоль туловища. Верните на место, далее повторите это же движение другой рукой. Чередуйте в умеренном темпе, паузы между сменами короткие. Корпусом не виляйте из стороны в сторону. Хорошее упражнение для укрепления рук, работает также весь кор, спина, грудь.", time = 40, category = "Для трицепса", gender = FEMALE))
        trainings.add(Training(image = R.drawable.icon_triceps_w_10, title = "Упражнение №10", subtitle = "Обратные отжимания + подтяжка ноги", description = "Сядьте на коврик, подогните на себя ноги и поставьте поближе к тазу. Отведите немного назад корпус, установите ладони на пол, пальцами к ягодицам. Прямой держите спину, не прогибайтесь и не сутультесь. Теперь чуть приподнимите таз от поверхности, пока не выпрямятся локти. Выполните отжимание, согните под углом почти 90 градусов руки и разогните обратно. Подтяните к животу правую ногу, поставьте на место. Снова повторите отжимание, а затем подтяжку другой ногой. В тренировке трицепсов для женщин это жиросжигающее упражнение активно прокачает как трехглавые мышцы рук, так и пресс, дополнительно подтянет ноги, ягодицы.", time = 30, category = "Для трицепса", gender = FEMALE))
        trainings.add(Training(image = R.drawable.icon_calf_w_1, title = "Упражнение №1", subtitle = "Подъем на носки с развернутыми стопами", description = "Поставьте ноги в первую танцевальную позицию, для этого соедините пятки и разведите носки. Руки положите на пояс или свободно опустите вниз. Из этого положения поднимитесь на пальцы, на мгновение задержитесь и опустите пятки вниз. Выполняйте движения в среднем темпе, напрягая икроножные мышцы в верхней точке. Балетное упражнение подойдет для похудения икр, а также поможет сделать ноги более рельефными.", time = 30, category = "Для икр и ног", gender = FEMALE))
        trainings.add(Training(image = R.drawable.icon_calf_w_2, title = "Упражнение №2", subtitle = "Плие-приседание с подъемом на носок", description = "Поставьте ноги как можно шире плеч, носки разведите в стороны, руки сложите у груди. Согните ноги в коленях, опускаясь в плие-приседание и одновременно ставя правую ногу на носок. Не отводите таз назад, держите спину ровно. Затем поднимитесь в исходное положение. На следующем подходе поднимайте на носок левую ногу. Чередуйте подъемы на носок при выполнении приседаний. Плие – это упражнение не только для стройных икр, но и для подтянутых бедер, так как отлично прорабатывает проблемные зоны.", time = 35, category = "Для икр и ног", gender = FEMALE))
        trainings.add(Training(image = R.drawable.icon_calf_w_3, title = "Упражнение №3", subtitle = "Махи ногой с подъемом на носки", description = "Встаньте прямо с опорой на левую ногу, правое колено немного развернуто в сторону. Руки лежат на поясе или опустите их вдоль тела. Теперь выполняйте мах правой ногой перед собой, одновременно поднимаясь левой ногой на носок при каждом движении вверх. Повторите целый подход на одну ногу. Можете держаться рукой за стул, чтобы легче держать равновесие. Это упражнение будет полезно для похудения икр, а также бедер и ягодиц, так как интенсивно прокачивает мышцы и делает ноги стройными.", time = 40, category = "Для икр и ног", gender = FEMALE))
        trainings.add(Training(image = R.drawable.icon_calf_w_4, title = "Упражнение №4", subtitle = "Подъем на носки в плие", description = "Поставьте ноги как можно шире, разведите носки в стороны. Опуститесь в плие-присед, сохраняя спину ровной, таз не отводите назад. Зафиксируйтесь в этом положении, а затем поставьте на носок правую ногу. Сразу же смените ногу. Чередуйте подъемы на носок каждой ногой, сохраняя стабильное положение в плие. Упражнение для стройных икр также глубоко нагружает мышцы бедер, в частности приводящие, укрепляя их и делая более гибкими. Отличное упражнение для подтягивания внутренней поверхности бедра.", time = 35, category = "Для икр и ног", gender = FEMALE))
        trainings.add(Training(image = R.drawable.icon_calf_w_5, title = "Упражнение №5", subtitle = "Пульсирующий подъем на носки", description = "Встаньте на полупальцы, слегка оторвав пятки от пола, руки положите на пояс или держитесь за стул. Теперь поднимитесь на носки, становясь только на пальцы, затем опуститесь в исходное положение, продолжая держать пятки на весу. Выполняйте пульсацию на носочках. Это упражнение отлично работает для похудения икр, но также подтягивает бедра за счет интенсивной статической нагрузки.", time = 40, category = "Для икр и ног", gender = FEMALE))
        trainings.add(Training(image = R.drawable.icon_calf_w_6, title = "Упражнение №6", subtitle = "Плие-приседание на носках", description = "Поставьте ноги немного шире плеч, носки разверните в стороны, руки держите в замке возле груди. Поднимитесь на носки и из этого положения выполняйте балетные приседание плие. Пятку на пол не опускайте на протяжении всего упражнения. Не приседайте слишком низко, основная задача – нагрузить икры. Балетное приседание плие – лучшее упражнение для стройных икр, так как отлично прокачивает мышцы голеней, формируя их красивую форму.", time = 30, category = "Для икр и ног", gender = FEMALE))
        trainings.add(Training(image = R.drawable.icon_calf_w_7, title = "Упражнение №7", subtitle = "Подъем на носки в глубоком наклоне", description = "Согните ноги в коленях и наклонитесь вперед, опираясь на ладони. Из этого положения поднимайтесь на носки, подтягивая колени ближе к рукам. Концентрируйтесь на работе целевых мышц, чтобы интенсивно нагрузить икры. Включайте это упражнение для похудения икр в план тренировок, чтобы бонусом получить стройные ноги и подтянутые ягодицы.", time = 30, category = "Для икр и ног", gender = FEMALE))
        trainings.add(Training(image = R.drawable.icon_calf_w_8, title = "Упражнение №8", subtitle = "Подъем на носки со стопами внутрь", description = "Встаньте на ширину плеч, носки поверните внутрь, чтобы пальцы ног были направлены друг к другу. Теперь ритмично поднимайтесь на носки, двигаясь с полной амплитудой. Почувствуйте, как напрягаются мышцы при каждом движении вверх. Руки положите на талию для удобства и равновесия. Это упражнение для стройных икр задействует их внутреннюю часть, подтягивая голени и формируя красивый рельеф ног.", time = 30, category = "Для икр и ног", gender = FEMALE))
        trainings.add(Training(image = R.drawable.icon_calf_w_9, title = "Упражнение №9", subtitle = "Плие-присед + подъем на носки", description = "Встаньте в первую танцевальную позицию, для этого разведите носки в стороны, а пятки поставьте вместе, руки положите на талию. Согните ноги в коленях, выполняя неглубокое балетное плие-приседание. В конечной точке поднимитесь на носки, затем выпрямите ноги и опуститесь на пятки. Выполняйте это балетное упражнение для стройных икр, чтобы интенсивно прокачать икроножные мышцы и сделать их более рельефными.", time = 30, category = "Для икр и ног", gender = FEMALE))
        trainings.add(Training(image = R.drawable.icon_calf_w_10, title = "Упражнение №10", subtitle = "Подъем на носки из собаки мордой вниз", description = "Встаньте в позицию йоги «собака мордой вниз», для этого из планки поднимите таз вверх, а затем выпрямите руки и ноги. Согните одну ногу в колене, поднимаясь на носок, а затем опустите пятку вниз, растягивая голень. Сразу же согните другую ногу в колене, повторяя движение. Упражнение растягивает икроножные мышцы, удлиняя их и делая эластичнее, что помогает добиться стройности и подтянутости ног.", time = 40, category = "Для икр и ног", gender = FEMALE))
        trainings.add(Training(image = R.drawable.icon_back_w_1, title = "Упражнение №1", subtitle = "Тяга гантели к поясу", description = "Возьмите гантель в левую руку и сделайте небольшой шаг вперед правой ногой. Немного согните обе ноги в коленях, одновременно наклоняя корпус вперед. Свободной рукой обопритесь о бедро передней ноги. Найдите устойчивое положение и выполните тягу гантели к поясу. Для этого согните левую руку в локте, направляя его вверх под прямым углом. Старайтесь тянуть спиной, не подключая активно дельтовидные и мышцы рук. Повторите другой рукой. Эффективное упражнение с гантелями воздействует на широчайшие мышцы спины, укрепляя их и способствуя росту.", time = 40, category = "Для спины", gender = FEMALE))
        trainings.add(Training(image = R.drawable.icon_back_w_2, title = "Упражнение №2", subtitle = "Тяга в наклоне обратным хватом", description = "Возьмите гантели обратным хватом (ладонями от себя), ноги поставьте на ширине плеч. Опустите руки вниз вдоль бедер, затем слегка согните ноги в коленях и наклоните корпус вперед. Держите спину ровной, для этого старайтесь прогибать поясницу и не опускать голову. Из исходного положения притяните гантели к корпусу, разводя локти в стороны и назад под прямым углом. В верхней точке сводите лопатки, а в нижней – не разгибайте локти полностью. Включайте упражнение в тренировку на спину с гантелями, чтобы проработать не только широчайшие, но также укрепить мышцы плечевого пояса и рук.", time = 30, category = "Для спины", gender = FEMALE))
        trainings.add(Training(image = R.drawable.icon_back_w_3, title = "Упражнение №3", subtitle = "Тяга к поясу", description = "Возьмите гантели прямым хватом (ладонями к себе), встаньте на ширине плеч. Руки опустите вниз на бедра, колени немного согните и наклонитесь вперед, не округляя спину. Для этого держите подбородок немного приподнятым, сохраняйте прогиб в пояснице. Притяните гантели к корпусу, при этом разводите локти в стороны под прямым углом. В верхней точке сводите лопатки для проработки центральной части спины. В нижней точке сохраняйте локти немного согнутыми. Базовое упражнение из тренировки на спину для женщин прорабатывает крупные мышечные группы: широчайшие, трапеции и ромбовидные.", time = 30, category = "Для спины", gender = FEMALE))
        trainings.add(Training(image = R.drawable.icon_back_w_4, title = "Упражнение №4", subtitle = "\"Доброе утро\" с гантелями", description = "Встаньте прямо и положите гантели на плечи, ноги слегка согните в коленях. Наклонитесь вперед, стараясь держать спину ровно. Для этого немного прогнитесь в пояснице, приподнимите подбородок и разверните плечи. Наклоняйтесь до ощущения растяжения подколенных сухожилий, а затем возвращайтесь в исходное положение. \"Доброе утро\" задействует нижнюю часть спины, в частности мышцы поясницы, и дополнительно прорабатывает заднюю поверхность бедер и ягодицы, что является отличным аргументом, чтобы включить упражнение в тренировки на регулярной основе.", time = 30, category = "Для спины", gender = FEMALE))
        trainings.add(Training(image = R.drawable.icon_back_w_5, title = "Упражнение №5", subtitle = "Разведения гантелей в наклоне", description = "Поставьте ноги на ширину плеч, гантели держите нейтральным хватом (параллельно друг другу). Слегка согните ноги в коленях и наклонитесь вперед, прогибая поясницу, голову не опускайте вниз. Сведите руки вместе, чтобы гантели соприкоснулись между собой. Немного согните руки в локтях, а затем разведите их в стороны до параллели с полом. В верхней точке сведите лопатки, а затем опустите руки вниз. Следите, чтобы локти двигались строго вверх, что обеспечит правильность техники. Силовое упражнение укрепляет широчайшие мышцы спины, а также ромбовидные, трапеции и зубчатые, способствуя развитию рельефа.", time = 30, category = "Для спины", gender = FEMALE))
        trainings.add(Training(image = R.drawable.icon_body_w_1, title = "Упражнение №1", subtitle = "Присед + жим руками вверх", description = "Встаньте ровно, расставьте стопы на удобное расстояние для приседа. В каждой кисти по гантели, руки согните на себя, чтобы снаряды оказались на плечах. По прямой линии держите корпус, смотрите вперед. Сделайте приседание обычной техникой, не заваливая колени внутрь и не выводя за носки, бедра опустите до параллели пола. Встаньте, выполните жим, для чего распрямите вверх над головой руки и опустите обратно. Это силовое упражнение для женщин дома прокачивает мышцы ягодиц, бедер, плеч и рук.", time = 30, category = "Для всего тела", gender = FEMALE))
        trainings.add(Training(image = R.drawable.icon_body_w_2, title = "Упражнение №2", subtitle = "Выпады назад + сгибания на бицепс", description = "Сохраните положение стоя, только стопы поставьте на ширину плеч. Возьмите в руки гантели, опустите их свободно вниз вдоль корпуса. Держите спину ровной линией, смотрите четко вперед. Выполните выпад назад, шагнув правой ногой, переднее колено над носком. На подъеме из выпада сделайте сгибание рук на себя, удерживая локти на талии. Распрямите руки, шагнув другой ногой в выпад. Задействуются мышцы ягодиц и бедер + бицепсы рук. Эффективное силовое упражнение для девушек дома, которое включает в работу все тело целиком.", time = 40, category = "Для всего тела", gender = FEMALE))
        trainings.add(Training(image = R.drawable.icon_body_w_3, title = "Упражнение №3", subtitle = "Тяга в наклоне + разгибания рук", description = "Останьтесь в положении обычной стойки со стопами на ширине плеч. После наклоните корпус вперед, отведя таз назад и немного согнув колени. Спина ровная, но можно оставить легкий прогиб в пояснице. Возьмите в кисти по гантели, опустите руки свободно вниз, ладони разверните друг к другу. Теперь выполните тягу к поясу: потяните локти назад за спину вдоль боков. Задержите в верхней точке руки, после их разогните и согните обратно, верните на прежнее место. Когда отводите руки назад, сводите лопатки. Такую тягу вносят в комплекс силовых упражнений для девушек по причине укрепления верха тела: прокачивается спина и трицепсы.", time = 30, category = "Для всего тела", gender = FEMALE))
        trainings.add(Training(image = R.drawable.icon_body_w_4, title = "Упражнение №4", subtitle = "Плие-присед + тяга к подбородку", description = "Расставьте широко стопы и разверните носки по сторонам. Поместите в каждой руке по гантели, свободно опустите их вниз. Выпрямитесь, смотрите вперед. По ровной линии присядьте, опустив таз. Бедра доведите до параллели пола, колени идут по направлению стоп. Корпус вперед не наклоняйте. Руки уходят между ногами по центру. Пока будете вставать, выполните протяжку гантелей, для чего согните вверх локти через стороны, доведя снаряды к верху груди. Опустите руки вниз, сделайте присед и повторите. В силовом упражнении для женщин дома на максимум работают приводящие мышцы бедер, ягодицы, мышцы плеч и рук.", time = 30, category = "Для всего тела", gender = FEMALE))
        trainings.add(Training(image = R.drawable.icon_body_w_5, title = "Упражнение №5", subtitle = "Перекрестный выпад + разводка гантелей", description = "Встаньте в обычную стойку, поставьте стопы рядом. Руки с гантелями опустите вниз вдоль туловища, далее согните под прямым углом в локтях, держите перед собой. В спине ровная линия. Выполните перекрестный выпад, для чего шагните правой ногой по диагонали назад и опустите таз. Доведите бедро до параллели пола, переднее колено не выводите за носок. Поднимитесь. По сторонам разведите руки, не меняя угол в локтях. Повторите с другой ногой. При выполнении выпадов не наклоняйтесь. В силовые упражнения для девушек дома вносят этот комплекс для оформления плеч, прорабатывания ягодиц и бицепсов бедер.", time = 40, category = "Для всего тела", gender = FEMALE))
        trainings.add(Training(image = R.drawable.icon_body_w_6, title = "Упражнение №6", subtitle = "Свинги с гантелями", description = "Ноги поставьте на расстояние, удобное для обычного приседа, носки поверните немного в стороны. Возьмите в руки гантели, опустите и сомкните ладони. Этот переход к исходной позиции дополните ровной спиной. Теперь выполните высокий присед: отведите таз назад и немного опустите вниз, бедра будут выше параллели пола, колени за стопы не заводите. Наклоните корпус. Руки должны идти между ногами. Начните распрямляться и одновременно выносить их вверх перед собой, доводя снаряды к уровню подбородка. Опуститесь снова, сделайте еще раз присед. Это интенсивное силовое упражнение для женщин дома полезно из-за усиления жиросжигания. Тратится много энергии, укрепляется низ тела, руки и плечи.", time = 30, category = "Для всего тела", gender = FEMALE))
        trainings.add(Training(image = R.drawable.icon_body_w_7, title = "Упражнение №7", subtitle = "Отжимания + тяга гантелей", description = "Перейдите в положения планки на прямых руках, взяв в кисти гантели. Мышцы живота напрягите, таз подберите. Стопы рядом, ладони под плечами. На ровной линии держите спину. Выполните отжимание, локти идут вдоль корпуса, опуститесь почти к полу и поднимитесь обратно. Теперь сделайте тягу к поясу, подняв правую руку и согнув в локте, затем то же самое сделайте левой рукой. Туловище не должно вестись по сторонам, зафиксируйте его четко. Такие функциональные отжимания включают в комплекс силовых упражнений для женщин, поскольку они задействует массу мышц: спину, грудь, пресс, кор, трицепсы и плечи.", time = 30, category = "Для всего тела", gender = FEMALE))
        trainings.add(Training(image = R.drawable.icon_body_w_8, title = "Упражнение №8", subtitle = "Скручивания на пресс с гантелью", description = "Перелягте на спину, плотно прижмитесь к полу корпусом. Подогните колени по ровной траектории на себя, поставьте стопы на ширине плеч. Обеими ладонями возьмите гантель, держите ее возле груди, руки согнуты в локтях. Без рывков, подконтрольно сделайте полное скручивание: поднимите корпус, потяните его вверх и доведите гантель до бедер. Затем плавно опустите обратно. Сильно спину не округляйте, снаряд не отпускайте далеко от себя. При возвращении укладывайте корпус полностью, вместе с головой. В силовом упражнении для женщин дома активно работает мускулатура живота. Укрепляются, тонизируются и очерчиваются мышцы.", time = 30, category = "Для всего тела", gender = FEMALE))
        trainings.add(Training(image = R.drawable.icon_body_w_9, title = "Упражнение №9", subtitle = "Жим гантелей + мостик", description = "Останьтесь лежать на спине, корпус плотно прижмите к поверхности. Возьмите по гантели в каждую ладонь, вытяните руки вверх над грудью, далее согните их через стороны и положите локти на пол, предплечья вертикальны. Подогните ноги на себя, поставьте стопы по ширине плеч. Начните одновременно выполнять два движения: жим руками вверх от груди и ягодичный мостик. Таз доводите до единой линии от корпуса и до бедер с коленями. Делайте упор на пятки и не подключайте спину. Работают грудь, плечевой пояс, мышцы ягодиц и ног, будет полезно включить в любой комплекс силовых упражнений для девушек.", time = 30, category = "Для всего тела", gender = FEMALE))
        trainings.add(Training(image = R.drawable.icon_body_w_10, title = "Упражнение №10", subtitle = "Подъемы корпуса + опускание ног", description = "Позицию лежа на спине сохраните, прижмитесь плотно к полу. Возьмите гантель ладонями с двух концов и выпрямите руки вверх на уровне груди. Поднимите прямые ноги вертикально вверх, зафиксируйте. Выполните подъем корпуса и вытяните руки, дотянувшись снарядом до стоп. Вернитесь в исходное положение, уложив на пол корпус, руки с гантелью вытянуты вверх. Теперь опустите ноги до пола и поднимите назад. Держите спину прижатой. Повторите еще раз оба движения. Не торопитесь, ощущайте в мышцах живот напряжение. Нагружается равномерно пресс, акценты идут на верх и низ. Это силовое упражнение для девушек дома отлично укрепляет всю мускулатуру кора.", time = 30, category = "Для всего тела", gender = FEMALE))
        trainings.add(Training(image = R.drawable.icon_press_w_1, title = "Упражнение №1", subtitle = "Подъемы рук к прямым ногам", description = "Разместитесь на коврике спиной вниз, хорошо вытянитесь и прижмитесь поясницей к полу. Теперь поднимите до вертикали прямые ноги. Таз при этом не отрывайте. Затем вытяните руки над грудью, ладони держите рядом друг с другом. Из этого положения начните делать основное движение. Приподнимите от коврика голову с верхом корпуса, дотянитесь ладонями до лодыжек, не сгибая локти. На всем протяжении напрягайте мышцы живота. Плавно опуститесь, повторите. Из упражнения выходит отличный способ прокачать верхнюю область пресса. Низ пресса также работает за счет поднятых ног.", time = 30, category = "Для пресса", gender = FEMALE))
        trainings.add(Training(image = R.drawable.icon_press_w_2, title = "Упражнение №2", subtitle = "Обратные скручивания с прямыми ногами", description = "Останьтесь в принятом положении лежа на спине, прямые ноги опустите, пятки держите над полом на небольшой высоте. Руки положите на пол. Теперь поднимите ноги вверх, перевалив за вертикаль и оторвав слегка таз от пола, при этом в конечной точке дополнительно их вытолкните. Плавно вернитесь назад в исходную позу и почти сразу, без длинной паузы, повторите подъем. Такие движения прорабатывают нижнюю часть живота, которая является проблемной зоной. Одно из эффективнейших упражнений на пресс у девушек для его подтяжки, похудения.", time = 30, category = "Для пресса", gender = FEMALE))
        trainings.add(Training(image = R.drawable.icon_press_w_3, title = "Упражнение №3", subtitle = "Скрестные подъемы рук и ног", description = "Сохраните положение на спине, только вытяните ноги и уложите на коврик, для завершения стартовой позы расправьте руки четко по сторонам. Прижмитесь к полу на всем протяжении туловища от головы до пяток. Сделайте скрестное движение в следующей технике: поднимите правую руку с верхом корпуса и прямую левую ногу, коснитесь ладонью голеностопа. Встреча должна быть на уровне таза, при этом остальные части тела зафиксированы. Опуститесь обратно и повторите, но уже поменяв стороны, левой рукой и правой ногой. Нагружаются в этом упражнении на пресс для женщин мышцы верха живота и низа, но акцент идет на косые пучки и зону талии.", time = 25, category = "Для пресса", gender = FEMALE))
        trainings.add(Training(image = R.drawable.icon_press_w_4, title = "Упражнение №4", subtitle = "Планка-паук с шагом", description = "Переместитесь в положение классической планки на прямых руках. Ладони под плечами, ровная спина, единая линия в туловище. Подберите живот и таз. Затем оторвите от пола правую ногу – согните, потяните колено через сторону к одноименному локтю. Стопа при этом касается пола, а корпус остается зафиксированным. Верните ногу назад, из исходной позы опять сделайте подтяжку, только уже левой ногой. Такому варианту планки причисляют массу полезных свойств: общее укрепление кора, прокачка низа пресса, усиление мелких стабилизаторов. Акцент же идет на бока и уменьшение жировой прослойки.", time = 25, category = "Для пресса", gender = FEMALE))
        trainings.add(Training(image = R.drawable.icon_press_w_5, title = "Упражнение №5", subtitle = "Складка бедра к животу", description = "Лягте снова на спину, плотно прижмитесь к поверхности. Расправьте руки по сторонам, положите их на коврик. Голову слегка приподнимите, ноги тоже держите на весу с небольшой высотой. Теперь сделайте складку – одновременно от пола оторвите корпус и ноги, подогните колени, притянитесь бедрами к животу, это должно произойти в вертикальной позиции. Опорой остаются ягодицы. Обеими руками обхватите голени, чтобы притянуться еще сильнее. Распрямитесь, снова повторите. Это отличное упражнение на пресс для девушек полезно тем, что максимально прокачивает мышечный корсет. Нагрузка будет концентрироваться на всей продольной и поперечной мускулатуре.", time = 30, category = "Для пресса", gender = FEMALE))
        trainings.add(Training(image = R.drawable.icon_press_w_6, title = "Упражнение №6", subtitle = "Косые скручивания колено-локоть", description = "Сохраните положение лежа на спине, плотно прижмитесь к коврику. Теперь за голову уберите руки, локти направьте по сторонам. Подогните колени, стопы в это же время придвиньте ближе к тазу. Сделайте скрестно подтяжку-скручивание: поднимите верх корпуса и правую ногу, дотяните левый локоть к правому колено, скрутив корпус. Медленно опуститесь обратно, при этом не расслабляйте полностью мускулатуру. Выполняйте целый подход на одну сторону, затем поменяйте. Упражнение на пресс отлично подходит женщинам, так как нагружается область мышц, формирующая талию. Активно работают верх и низ живота.", time = 45, category = "Для пресса", gender = FEMALE))
        trainings.add(Training(image = R.drawable.icon_press_w_7, title = "Упражнение №7", subtitle = "Велосипед с опорой на предплечья", description = "Поднимитесь и упритесь на локти с предплечьями из положения на спине. Руки направьте кистями к тазу, спину держите ровной, с легким изгибом поясницы, а ноги приподнимите от пола, оставьте на весу. Начните поочередно притягивать бедра к себе, сгибая колени. Доводите до вертикали, дальше не нужно. Все это время пятки не должны касаться поверхности. Темп поддерживайте средний. За ногами не двигайте корпусом, зафиксируйте положение. Идет акцентированная нагрузка для нижней зоны пресса, дополнительно прокачиваются мелкие стабилизаторы.", time = 30, category = "Для пресса", gender = FEMALE))
        trainings.add(Training(image = R.drawable.icon_press_w_8, title = "Упражнение №8", subtitle = "Подъемы таза в боковой планке", description = "Перевернитесь на левый бок, упритесь на локоть и предплечье, стопы положите друг на друга. Таз опирается на пол, в корпусе ровная линия, не заваливайте корпус вперед. Свободную руку расположите на талии. Теперь сделайте подъем таза вверх, двигайтесь в одной плоскости со всем остальным телом. При возвращении не опускайтесь полностью на пол, едва касайтесь пола. После целого подхода поменяйте стороны, перевернувшись на правый бок. Не самое легкое упражнение на пресс для девушек, зато эффективное, так как прокачивает кор, его боковые пучки.", time = 45, category = "Для пресса", gender = FEMALE))
        return trainings
    }


    @SuppressLint("ResourceType")
